// Quality Control & Production Finalization
const productionResults = $input.all();
const timestamp = new Date().toISOString();

// Aggregate all production line results
const aggregatedResults = {
  factory_completion_time: timestamp,
  total_production_lines: productionResults.length,
  successful_productions: 0,
  failed_productions: 0,
  content_inventory: {}
};

// Process each production line result
productionResults.forEach((result, index) => {
  try {
    const resultData = result.json;

    // Parse AI-generated content
    let contentData;
    if (resultData.choices && resultData.choices[0] && resultData.choices[0].message) {
      contentData = JSON.parse(resultData.choices[0].message.content);
      aggregatedResults.successful_productions++;
    } else {
      aggregatedResults.failed_productions++;
      return;
    }

    // Determine content type based on structure
    let contentType = 'unknown';
    if (contentData.title && contentData.content) {
      contentType = 'blog_post';
    } else if (contentData.linkedin || contentData.facebook || contentData.twitter) {
      contentType = 'social_media';
    } else if (contentData.subject_lines && contentData.email_body) {
      contentType = 'email_campaign';
    }

    // Quality assessment
    const qualityMetrics = {
      content_completeness: contentData ? 95 : 0,
      seo_optimization: contentData.seo_keywords ? 90 : 50,
      readability_score: contentData.readability_score || 75,
      engagement_potential: 85,
      brand_compliance: 90
    };

    const overallQuality = Object.values(qualityMetrics).reduce((sum, score) => sum + score, 0) / Object.keys(qualityMetrics).length;

    // Add to content inventory
    aggregatedResults.content_inventory[contentType] = {
      content_data: contentData,
      quality_metrics: qualityMetrics,
      overall_quality_score: overallQuality,
      production_timestamp: timestamp,
      ready_for_publication: overallQuality >= 80,
      requires_review: overallQuality < 80
    };

  } catch (error) {
    aggregatedResults.failed_productions++;
  }
});

// Calculate factory performance metrics
const factoryPerformance = {
  success_rate: ((aggregatedResults.successful_productions / aggregatedResults.total_production_lines) * 100).toFixed(1) + '%',
  average_quality_score: Object.values(aggregatedResults.content_inventory)
    .reduce((sum, item) => sum + (item.overall_quality_score || 0), 0) / aggregatedResults.successful_productions || 0,
  production_efficiency: aggregatedResults.successful_productions > 0 ? 'high' : 'low',
  ready_for_publication: Object.values(aggregatedResults.content_inventory)
    .filter(item => item.ready_for_publication).length
};

// Next actions based on results
const nextActions = [];
if (aggregatedResults.content_inventory.blog_post?.ready_for_publication) {
  nextActions.push('publish_blog_post');
  nextActions.push('trigger_social_distribution');
}
if (aggregatedResults.content_inventory.social_media?.ready_for_publication) {
  nextActions.push('schedule_social_posts');
}
if (aggregatedResults.content_inventory.email_campaign?.ready_for_publication) {
  nextActions.push('deploy_email_sequence');
}

// Content distribution plan
const distributionPlan = {
  immediate_actions: nextActions,
  scheduled_actions: [
    {
      action: 'cross_platform_promotion',
      scheduled_for: new Date(Date.now() + 30 * 60 * 1000).toISOString() // 30 minutes
    },
    {
      action: 'performance_tracking_start',
      scheduled_for: new Date(Date.now() + 60 * 60 * 1000).toISOString() // 1 hour
    }
  ],
  analytics_tracking: {
    enabled: true,
    metrics: ['views', 'engagement', 'conversions', 'shares'],
    reporting_frequency: 'daily'
  }
};

// Final factory report
const factoryReport = {
  factory_session_id: `factory_${Date.now()}`,
  production_summary: aggregatedResults,
  performance_metrics: factoryPerformance,
  content_inventory: aggregatedResults.content_inventory,
  distribution_plan: distributionPlan,
  factory_status: 'PRODUCTION_COMPLETE',
  total_content_pieces: Object.keys(aggregatedResults.content_inventory).length,
  recommendations: [
    'Monitor content performance',
    'Optimize based on engagement data',
    'Scale successful content types',
    'Continue quality improvement'
  ]
};

return { json: factoryReport };
